import React, { Component } from 'react';
import { useHistory } from "react-router-dom";
import ReactDOM from 'react-dom';
import './Login.css';
import { Redirect, Route } from "react-router";


class Login extends React.Component{
    constructor(props) {
        super(props)
        this.state = { username: '', password: '', loggedinstate: '', loginStatus: false }
      }  

      componentDidMount(){
        //this.isLoggedIn();

      }

    saveLogin(username, password) {
      return new Promise((resolve, reject) => {
        fetch('/login/',{
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({"username": username, "password": password}),
        });
      })

    }

    wasSucess = () => {
      fetch('/login/',{ method: 'GET'})
      .then((response) => response.json())
      .then((data) => {
        console.log(data)
        this.setState({loginStatus: data['status']})
      });
  }


  isLoggedIn = () => {
		fetch('/login-status/')
		.then((response) => response.json())
		.then((data) => this.setState({loggedinstate: data}));

}

    handleChange(event) {
      let name = event.target.name;
      if (name == 'username'){
        this.setState({ username: event.target.value})
      }
      if (name == 'password'){
        this.setState({ password: event.target.value})
      }

    }

    async handleSubmit(event) {
        event.preventDefault()
        await this.saveLogin(this.state.username, this.state.password, this.wasSucess)
        await this.wasSucess();
        //this.isLoggedIn()
        console.log(this.state.loginStatus)
      }




      render() {
        if (this.state.loginStatus) {
          return <Redirect to="/" />
        }
        return (
          <form onSubmit={this.handleSubmit.bind(this)} className='form-group'>
            <h1>Sign In</h1>
            <label>Username</label>
            <input
              type="text"
              value={this.state.username}
              onChange={this.handleChange.bind(this)}
              name='username'
            /><br></br>
            <span>Password</span>
            <input
              type="password"
              value={this.state.password}
              onChange={this.handleChange.bind(this)}
              name='password'
              required
            />

            <input type="submit" value="Submit" />
          </form>
        )
      }
}

export default Login;
